function* boll(C){
    //指标定义
    C.DEFINE({
        type: "MAIN",
        cname: "布林带",
        state: "KLINE",
    });
    //参数
    let n = C.PARAM(3, "N", {memo: "按N个周期计算均值与标准差"});
    let p = C.PARAM(5, "P", {memo: "上下轨与中线的距离为P个标准差"});
    //输入序列
    let close = C.SERIAL("CLOSE");
    //输出序列
    let top = C.OUTS("LINE", "top", {color: YELLOW});
    let bottom = C.OUTS("LINE", "bottom", {color: LIGHTRED});
    //临时序列
    let mid = [];
    let std = [];
    //计算
    while(true) {
        let i = yield;
        mid[i] = MA(i, close, n, mid);
        std[i] = STDEV(i, close, n, std);
        top[i] = mid[i] + p * std[i];
        bottom[i] = mid[i] - p * std[i];
    }
}

kdj:
function* kdj(C){
    //指标定义
    C.DEFINE({
        type: "SUB",
        cname: "KDJ",
        state: "KLINE",
    });
    //参数
    let n = C.PARAM(3, "N");
    let m1 = C.PARAM(5, "M1");
    let m2 = C.PARAM(5, "M2");
    //输入序列
    let close = C.SERIAL("CLOSE");
    let high = C.SERIAL("HIGH");
    let low = C.SERIAL("LOW");
    //输出序列
    let k = C.OUTS("LINE", "k", {color: RED});
    let d = C.OUTS("LINE", "d", {color: GREEN});
    let j = C.OUTS("LINE", "j", {color: YELLOW});
    //临时序列
    let rsv = [];
    //计算
    while(true) {
        let i = yield;
        let hv = HIGHEST(i, high, n);
        let lv = LOWEST(i, low, n);
        rsv[i] = (hv == lv) ? 0 : (close[i] - lv) / (hv - lv) * 100;
        k[i] = SMA(i, rsv, m1, 1, k);
        d[i] = SMA(i, k, m2, 1, d);
        j[i] = 3*k[i] -2*d[i];
    }
}

ma:
function* ma(C){
    //指标定义
    C.DEFINE({
        type: "MAIN",
        cname: "均线组",
        state: "KLINE",
    });
    //参数
    let n1 = C.PARAM(3, "N1");
    let n2 = C.PARAM(5, "N2");
    let n3 = C.PARAM(10, "N3");
    let n4 = C.PARAM(20, "N4");
    //输入序列
    let s = C.SERIAL("CLOSE");
    //输出序列
    let s1 = C.OUTS("LINE", "ma" + n1, {color: RED});
    let s2 = C.OUTS("LINE", "ma" + n2, {color: GREEN});
    let s3 = C.OUTS("LINE", "ma" + n3, {color: BLUE});
    let s4 = C.OUTS("LINE", "ma" + n4, {color: YELLOW});
    //计算
    while(true) {
        let i = yield;
        s1[i] = MA(i, s, n1, s1);
        s2[i] = MA(i, s, n2, s2);
        s3[i] = MA(i, s, n3, s3);
        s4[i] = MA(i, s, n4, s4);
    }
}

macd:
function* macd(C) {
    //指标定义
    C.DEFINE({
        type: "SUB",
        cname: "MACD",
        state: "KLINE",
        yaxis: [
            {id: 0, mid: 0, format: "NUMBER2"},
        ]
    });
    //参数
    let vshort = C.PARAM(20, "SHORT", {min: 1, memo:"短周期"});
    let vlong = C.PARAM(35, "LONG", {min: 2, memo:"长周期"});
    let vm = C.PARAM(10, "M", {min: 1});
    //输入序列
    let sclose = C.SERIAL("CLOSE");
    //输出序列
    let diff = C.OUTS("LINE", "diff", {color: WHITE});
    let dea = C.OUTS("LINE", "dea", {color: YELLOW, width: 1});
    let bar = C.OUTS("RGBAR", "bar");
    //临时序列
    let eshort = [];
    let elong = [];
    //计算
    while(true) {
        let i = yield;
        eshort[i] = EMA(i, sclose, vshort, eshort);
        elong[i] = EMA(i, sclose, vlong, elong);
        diff[i] = eshort[i] - elong[i];
        dea[i] = EMA(i, diff, vm, dea);
        bar[i] = 2 * (diff[i] - dea[i]);
    }
}

